import { CommonModule } from "@angular/common";
import {
  AfterViewInit,
  Component,
  ElementRef,
  NgZone,
  QueryList,
  ViewChildren,
} from "@angular/core";
import gsap from "gsap";

@Component({
  selector: "app-hero",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./hero.component.html",
  styleUrl: "./hero.component.css",
})
export class HeroComponent implements AfterViewInit {
  readonly firstNameLetters = "MAXIME".split("");
  readonly lastNameLetters = "FARRUGGIA".split("");
  readonly chips = [
    "Montage Vidéo",
    "Développement Web",
    "3D",
    "Motion/Graphic Design",
  ];

  @ViewChildren("heroWord")
  private heroWords!: QueryList<ElementRef<HTMLElement>>;

  @ViewChildren("heroLetter")
  private heroLetters!: QueryList<ElementRef<HTMLElement>>;

  private viewInitialized = false;
  private animationQueued = false;

  constructor(private readonly zone: NgZone) {}

  ngAfterViewInit(): void {
    this.viewInitialized = true;

    this.zone.runOutsideAngular(() => {
      this.heroWords.forEach((word: ElementRef<HTMLElement>) => {
        word.nativeElement.style.visibility = "hidden";
        gsap.set(word.nativeElement, { letterSpacing: "0rem", opacity: 0 });
      });
      this.heroLetters.forEach((letter: ElementRef<HTMLElement>) => {
        gsap.set(letter.nativeElement, { opacity: 0 });
      });
    });

    if (this.animationQueued) {
      this.animationQueued = false;
      this.playIntroAnimation();
    }
  }

  animateIntro(): void {
    this.animationQueued = true;

    if (!this.viewInitialized) {
      return;
    }

    this.animationQueued = false;
    this.playIntroAnimation();
  }

  private playIntroAnimation(): void {
    this.zone.runOutsideAngular(() => {
      const words = this.heroWords
        .toArray()
        .map((word: ElementRef<HTMLElement>) => word.nativeElement);
      const letters = this.heroLetters
        .toArray()
        .map((letter: ElementRef<HTMLElement>) => letter.nativeElement);

      if (!words.length || !letters.length) {
        return;
      }

      words.forEach((word: HTMLElement) => {
        word.style.visibility = "visible";
      });

      gsap.set(words, { letterSpacing: "0rem", opacity: 0 });
      gsap.set(letters, { opacity: 0 });

      gsap.to(letters, {
        opacity: 1,
        duration: 0.4,
        stagger: 0.05,
      });

      gsap.to(words, {
        letterSpacing: "1.2rem",
        opacity: 1,
        duration: 0.8,
        ease: "power2.out",
        stagger: 0.1,
        onComplete: () => {
          gsap.to(words, {
            letterSpacing: "0.6rem",
            duration: 0.6,
            ease: "elastic.out(1, 0.6)",
            stagger: 0.1,
          });
        },
      });
    });
  }
}
<section
  id="presentation"
  class="container mx-auto px-6 py-16 flex flex-col md:flex-row items-start gap-8"
>
  <div class="w-full md:w-2/3 relative">
    <img
      src="svg/fondpres.svg"
      alt="fond"
      class="hidden md:block absolute left-0 top-0 opacity-10 pointer-events-none"
    />

    <h1
      id="heroName"
      class="text-6xl md:text-8xl font-extralight leading-tight tracking-wide uppercase"
    >
      <ng-container
        *ngFor="
          let letters of [firstNameLetters, lastNameLetters];
          let i = index
        "
      >
        <span class="hero-word block" [class.mt-8]="i === 1" #heroWord>
          <span
            *ngFor="let letter of letters"
            class="hero-letter inline-block"
            #heroLetter
          >
            {{ letter }}
          </span>
        </span>
      </ng-container>
    </h1>

    <div class="mt-12 grid grid-cols-2 gap-6 max-w-2xl">
      <ng-container *ngFor="let chip of chips">
        <div
          class="px-6 py-3 bg-accent/10 rounded-full shadow-inner text-accent/95 tracking-widest"
          [class.w-24]="chip === '3D'"
        >
          {{ chip }}
        </div>
      </ng-container>
    </div>
  </div>

  <aside class="w-full md:w-1/3">
    <div class="bg-accent/6 p-6 rounded-2xl backdrop-blur-sm">
      <h3 class="text-lg text-accent mb-3">À propos</h3>
      <p class="text-sm text-accent/90">
        Je suis Maxime, motion designer &amp; développeur web. J'explore le
        design d'interfaces, la vidéo et l'expérience utilisateur.
      </p>
      <div class="mt-4 flex gap-3">
        <a
          href="#contact"
          class="text-sm bg-accent text-primary px-3 py-2 rounded-full"
        >
          Contactez-moi
        </a>
      </div>
    </div>
  </aside>
</section>
