import { CommonModule } from "@angular/common";
import { AfterViewInit, Component, NgZone, ViewChild } from "@angular/core";
import gsap from "gsap";
import { CompetencesComponent } from "./components/competences/competences.component";
import { ContactComponent } from "./components/contact/contact.component";
import { HeaderComponent } from "./components/header/header.component";
import { HeroComponent } from "./components/hero/hero.component";
import { IntroOverlayComponent } from "./components/intro-overlay/intro-overlay.component";
import { ProjectsComponent } from "./components/projects/projects.component";
import { SiteFooterComponent } from "./components/site-footer/site-footer.component";
import { TimelineComponent } from "./components/timeline/timeline.component";

@Component({
  selector: "app-root",
  standalone: true,
  imports: [
    CommonModule,
    IntroOverlayComponent,
    HeaderComponent,
    HeroComponent,
    CompetencesComponent,
    ProjectsComponent,
    TimelineComponent,
    ContactComponent,
    SiteFooterComponent,
  ],
  templateUrl: "./app.component.html",
  styleUrl: "./app.component.css",
})
export class AppComponent implements AfterViewInit {
  introVisible = true;

  @ViewChild(HeroComponent) private heroComponent?: HeroComponent;

  constructor(private readonly zone: NgZone) {}

  ngAfterViewInit(): void {
    this.prepareSectionsForReveal();
    this.setupSectionObserver();
  }

  handleIntroClosed(): void {
    this.introVisible = false;
    this.heroComponent?.animateIntro();
  }

  private prepareSectionsForReveal(): void {
    this.zone.runOutsideAngular(() => {
      const sections = Array.from(
        document.querySelectorAll<HTMLElement>("main section")
      );
      sections.forEach((section) => {
        gsap.set(section, { autoAlpha: 0, y: 20 });
      });
    });
  }

  private setupSectionObserver(): void {
    this.zone.runOutsideAngular(() => {
      const sections = Array.from(
        document.querySelectorAll<HTMLElement>("main section")
      );
      const observer = new IntersectionObserver(
        (entries: IntersectionObserverEntry[]) => {
          entries.forEach((entry: IntersectionObserverEntry) => {
            if (entry.isIntersecting) {
              gsap.to(entry.target, {
                autoAlpha: 1,
                y: 0,
                duration: 0.8,
                ease: "power2.out",
              });
              observer.unobserve(entry.target);
            }
          });
        },
        { threshold: 0.12 }
      );

      sections.forEach((section) => observer.observe(section));
    });
  }
}
<app-intro-overlay
  *ngIf="introVisible"
  (closed)="handleIntroClosed()"
></app-intro-overlay>
<app-header></app-header>
<main class="min-h-screen">
  <app-hero></app-hero>
  <app-competences></app-competences>
  <app-projects></app-projects>
  <app-timeline></app-timeline>
  <app-contact></app-contact>
</main>
<app-site-footer></app-site-footer>
