import { CommonModule } from "@angular/common";
import { Component } from "@angular/core";
import { DomSanitizer, SafeResourceUrl } from "@angular/platform-browser";

type ProjectCategory = "video" | "graphic" | "threeD" | "web";

type VideoSection = {
  title: string;
  items: Array<{
    title: string;
    description: string;
    embedUrl: string;
  }>;
};

type CarouselSlide = {
  title: string;
  description: string;
  link?: string;
};

type Carousel = {
  title: string;
  slides: CarouselSlide[];
};

@Component({
  selector: "app-projects",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./projects.component.html",
  styleUrl: "./projects.component.css",
})
export class ProjectsComponent {
  readonly categoryOptions: Array<{ label: string; value: ProjectCategory }> = [
    { label: "Montage vidéo / Motion design", value: "video" },
    { label: "Graphic design", value: "graphic" },
    { label: "3D", value: "threeD" },
    { label: "Développement web", value: "web" },
  ];

  readonly videoSections: VideoSection[] = [
    {
      title: "Montage vidéo",
      items: [
        {
          title: "Aftermovie - Placeholder",
          description:
            "Montage dynamique avec transitions rythmées et sound design immersif.",
          embedUrl: "https://www.youtube.com/embed/dQw4w9WgXcQ",
        },
        {
          title: "Interview - Placeholder",
          description:
            "Montage d'interview avec habillage graphique et sous-titrage.",
          embedUrl: "https://www.youtube.com/embed/oHg5SJYRHA0",
        },
      ],
    },
    {
      title: "Motion design",
      items: [
        {
          title: "Explainer animé - Placeholder",
          description:
            "Séquence motion design illustrant un produit digital en flat design.",
          embedUrl: "https://www.youtube.com/embed/2vjPBrBU-TM",
        },
        {
          title: "Identité animée - Placeholder",
          description:
            "Animation de logo et typographie cinétique pour une direction artistique.",
          embedUrl: "https://www.youtube.com/embed/aqz-KE-bpKQ",
        },
      ],
    },
  ];

  readonly graphicCarousels: Carousel[] = [
    {
      title: "Identités visuelles",
      slides: [
        {
          title: "Branding café",
          description:
            "Palette douce, packaging éco-responsable et déclinaisons réseaux.",
        },
        {
          title: "Rebrand tech",
          description:
            "Système modulaire, pictogrammes sur mesure et guidelines complètes.",
        },
        {
          title: "Studio photo",
          description:
            "Logo minimaliste, monogramme et brochures pour un studio créatif.",
        },
      ],
    },
    {
      title: "Affiches & print",
      slides: [
        {
          title: "Festival musique",
          description:
            "Affiche typographique avec textures grainées et déclinaisons web.",
        },
        {
          title: "Expo immersive",
          description:
            "Série d'affiches néon avec effets de profondeur et QR codes.",
        },
        {
          title: "Campagne associative",
          description:
            "Set de flyers et roll-ups en double langue, mise en page accessible.",
        },
      ],
    },
    {
      title: "Social media",
      slides: [
        {
          title: "Stories mode",
          description:
            "Animations verticales, typographie audacieuse et filtres photo.",
        },
        {
          title: "Lancements produit",
          description: "Carrousels Instagram avec mockups 3D et CTA optimisés.",
        },
        {
          title: "Calendrier éditorial",
          description:
            "Templates adaptables pour publications LinkedIn & TikTok.",
        },
      ],
    },
  ];

  readonly threeDCarousels: Carousel[] = [
    {
      title: "Modélisation",
      slides: [
        {
          title: "Mobilier futuriste",
          description:
            "Concept chaise générée dans Blender avec matériaux PBR.",
        },
        {
          title: "Architecture low-poly",
          description: "Scène urbaine stylisée optimisée pour le temps réel.",
        },
        {
          title: "VFX props",
          description: "Accessoires sci-fi, UV clean et textures 4K.",
        },
      ],
    },
    {
      title: "Rendu & lighting",
      slides: [
        {
          title: "Still life produits",
          description:
            "Set luxe avec HDRI, caustiques et depth of field réaliste.",
        },
        {
          title: "Environnement nocturne",
          description:
            "Lighting volumétrique et post-process dans Unreal Engine.",
        },
        {
          title: "Character rim light",
          description: "Portrait 3D stylisé, shading peau et rim dramatique.",
        },
      ],
    },
  ];

  readonly webCarousels: Carousel[] = [
    {
      title: "Applications web",
      slides: [
        {
          title: "Dashboard analytics",
          description: "Interface responsive avec charts custom et dark mode.",
          link: "https://example.com/dashboard",
        },
        {
          title: "Plateforme e-learning",
          description: "Parcours utilisateur gamifié, accessibilité niveau AA.",
          link: "https://example.com/e-learning",
        },
        {
          title: "Portail évènementiel",
          description: "Intégration Angular & API headless pour billetterie.",
          link: "https://example.com/event",
        },
      ],
    },
    {
      title: "Sites vitrines",
      slides: [
        {
          title: "Studio créatif",
          description:
            "Animations GSAP, typographie variable et assets optimisés.",
          link: "https://example.com/studio",
        },
        {
          title: "Restaurant gastronomique",
          description:
            "Menu interactif, réservation en ligne et micro-interactions.",
          link: "https://example.com/restaurant",
        },
        {
          title: "Portfolio photo",
          description: "Galerie masonry, lazy loading et mode clair/sombre.",
          link: "https://example.com/photo",
        },
      ],
    },
  ];

  readonly graphicIndices = this.graphicCarousels.map(() => 0);
  readonly threeDIndices = this.threeDCarousels.map(() => 0);
  readonly webIndices = this.webCarousels.map(() => 0);

  activeCategory: ProjectCategory = "video";

  constructor(private readonly sanitizer: DomSanitizer) {}

  selectCategory(category: ProjectCategory): void {
    this.activeCategory = category;
  }

  previousSlide(group: "graphic" | "threeD" | "web", index: number): void {
    const { carousels, indices } = this.resolveGroup(group);
    const total = carousels[index].slides.length;
    indices[index] = (indices[index] - 1 + total) % total;
  }

  nextSlide(group: "graphic" | "threeD" | "web", index: number): void {
    const { carousels, indices } = this.resolveGroup(group);
    const total = carousels[index].slides.length;
    indices[index] = (indices[index] + 1) % total;
  }

  getSlide(group: "graphic" | "threeD" | "web", index: number): CarouselSlide {
    const { carousels, indices } = this.resolveGroup(group);
    return carousels[index].slides[indices[index]];
  }

  toSafeUrl(url: string): SafeResourceUrl {
    return this.sanitizer.bypassSecurityTrustResourceUrl(url);
  }

  private resolveGroup(group: "graphic" | "threeD" | "web") {
    switch (group) {
      case "graphic":
        return {
          carousels: this.graphicCarousels,
          indices: this.graphicIndices,
        };
      case "threeD":
        return { carousels: this.threeDCarousels, indices: this.threeDIndices };
      case "web":
      default:
        return { carousels: this.webCarousels, indices: this.webIndices };
    }
  }
}
<section id="projets" class="container mx-auto px-6 py-20">
  <div class="max-w-6xl mx-auto">
    <div class="mb-12">
      <h2 class="text-3xl mb-4">Projets</h2>
      <p class="text-white/60 text-base">
        Sélection de réalisations organisées par discipline. Choisissez une
        catégorie pour explorer mes productions.
      </p>
    </div>

    <div class="flex flex-wrap gap-3 mb-12">
      <button
        *ngFor="let option of categoryOptions"
        type="button"
        class="px-6 py-2 rounded-full text-sm tracking-wide transition-all duration-200 border border-white/10"
        [ngClass]="
          activeCategory === option.value
            ? 'bg-accent text-primary shadow-lg shadow-accent/20'
            : 'bg-white/5 text-white/70 hover:bg-white/10'
        "
        (click)="selectCategory(option.value)"
      >
        {{ option.label }}
      </button>
    </div>

    <div *ngIf="activeCategory === 'video'" class="space-y-12">
      <div *ngFor="let section of videoSections" class="space-y-6">
        <h3 class="text-2xl">{{ section.title }}</h3>
        <div class="grid gap-8 md:grid-cols-2">
          <article
            *ngFor="let item of section.items"
            class="p-6 bg-white/5 border border-white/10 rounded-2xl backdrop-blur-sm flex flex-col gap-4"
          >
            <div class="aspect-video rounded-xl overflow-hidden bg-black/60">
              <iframe
                class="w-full h-full"
                frameborder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowfullscreen
                [src]="toSafeUrl(item.embedUrl)"
                title="YouTube placeholder"
              ></iframe>
            </div>
            <div>
              <h4 class="text-lg">{{ item.title }}</h4>
              <p class="text-white/70 text-sm leading-relaxed">
                {{ item.description }}
              </p>
            </div>
          </article>
        </div>
      </div>
    </div>

    <div *ngIf="activeCategory === 'graphic'" class="space-y-10">
      <div
        *ngFor="let carousel of graphicCarousels; let idx = index"
        class="p-6 bg-white/5 border border-white/10 rounded-2xl backdrop-blur-sm"
      >
        <div class="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
          <h3 class="text-2xl">{{ carousel.title }}</h3>
          <div class="flex gap-3">
            <button
              type="button"
              class="carousel-nav"
              (click)="previousSlide('graphic', idx)"
            >
              ‹
            </button>
            <button
              type="button"
              class="carousel-nav"
              (click)="nextSlide('graphic', idx)"
            >
              ›
            </button>
          </div>
        </div>
        <div class="relative overflow-hidden rounded-xl bg-accent/5 p-6 min-h-[220px] flex flex-col justify-between">
          <div>
            <p class="text-sm text-accent/70 uppercase tracking-[0.4em] mb-3">
              Slide {{ graphicIndices[idx] + 1 }} / {{ carousel.slides.length }}
            </p>
            <h4 class="text-xl mb-2">
              {{ getSlide('graphic', idx).title }}
            </h4>
            <p class="text-white/70 text-sm leading-relaxed">
              {{ getSlide('graphic', idx).description }}
            </p>
          </div>
          <span class="self-end text-xs text-white/40">Moodboard &amp; maquettes</span>
        </div>
      </div>
    </div>

    <div *ngIf="activeCategory === 'threeD'" class="space-y-10">
      <div
        *ngFor="let carousel of threeDCarousels; let idx = index"
        class="p-6 bg-white/5 border border-white/10 rounded-2xl backdrop-blur-sm"
      >
        <div class="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
          <h3 class="text-2xl">{{ carousel.title }}</h3>
          <div class="flex gap-3">
            <button
              type="button"
              class="carousel-nav"
              (click)="previousSlide('threeD', idx)"
            >
              ‹
            </button>
            <button
              type="button"
              class="carousel-nav"
              (click)="nextSlide('threeD', idx)"
            >
              ›
            </button>
          </div>
        </div>
        <div class="relative overflow-hidden rounded-xl bg-gradient-to-br from-white/10 via-white/5 to-white/0 p-6 min-h-[220px] flex flex-col justify-between">
          <div>
            <p class="text-sm text-accent/70 uppercase tracking-[0.4em] mb-3">
              Slide {{ threeDIndices[idx] + 1 }} / {{ carousel.slides.length }}
            </p>
            <h4 class="text-xl mb-2">
              {{ getSlide('threeD', idx).title }}
            </h4>
            <p class="text-white/70 text-sm leading-relaxed">
              {{ getSlide('threeD', idx).description }}
            </p>
          </div>
          <span class="self-end text-xs text-white/40">Rendus &amp; lighting</span>
        </div>
      </div>
    </div>

    <div *ngIf="activeCategory === 'web'" class="space-y-10">
      <div
        *ngFor="let carousel of webCarousels; let idx = index"
        class="p-6 bg-white/5 border border-white/10 rounded-2xl backdrop-blur-sm"
      >
        <div class="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
          <h3 class="text-2xl">{{ carousel.title }}</h3>
          <div class="flex gap-3">
            <button
              type="button"
              class="carousel-nav"
              (click)="previousSlide('web', idx)"
            >
              ‹
            </button>
            <button
              type="button"
              class="carousel-nav"
              (click)="nextSlide('web', idx)"
            >
              ›
            </button>
          </div>
        </div>
        <div class="relative overflow-hidden rounded-xl bg-white/5 p-6 min-h-[220px] flex flex-col justify-between">
          <div>
            <p class="text-sm text-accent/70 uppercase tracking-[0.4em] mb-3">
              Slide {{ webIndices[idx] + 1 }} / {{ carousel.slides.length }}
            </p>
            <h4 class="text-xl mb-2">
              {{ getSlide('web', idx).title }}
            </h4>
            <p class="text-white/70 text-sm leading-relaxed mb-4">
              {{ getSlide('web', idx).description }}
            </p>
          </div>
          <div class="flex justify-end">
            <a
              *ngIf="getSlide('web', idx).link as url"
              class="inline-flex items-center gap-2 bg-accent text-primary text-sm font-semibold px-4 py-2 rounded-full shadow-lg shadow-accent/20 transition-transform duration-200 hover:scale-105"
              [href]="url"
              target="_blank"
              rel="noopener noreferrer"
            >
              Voir le site
              <span aria-hidden="true">↗</span>
            </a>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
