import { CommonModule } from "@angular/common";
import { Component } from "@angular/core";

type TimelineEntry = {
  title: string;
  organisation: string;
  period: string;
  details: string;
};

@Component({
  selector: "app-timeline",
  standalone: true,
  imports: [CommonModule],
  templateUrl: "./timeline.component.html",
  styleUrl: "./timeline.component.css",
})
export class TimelineComponent {
  readonly experiences: TimelineEntry[] = [
    {
      title: "Stage – Développement web",
      organisation: "Entreprise Vassil Sculptures à Meaux",
      period: "Juin 2025 – Août 2025",
      details:
        "Refonte entière d'un site Web en HTML, CSS, JavaScript, PHP et MySQL (plus de détails sur ce stage dans mes projets).",
    },
    {
      title: "Stage de découverte de l'INA",
      organisation: "Fondation diversité et culture, égalité des chances.",
      period: "Mai 2024",
      details:
        "J'ai eu l'immense chance de pouvoir assister à ce stage lors de mon année de Terminale. Le but était de découvrir le monde de l'audiovisuel et de la production à travers le bâtiment de l'INA à Paris.",
    },
    {
      title: "Stage de 3ème",
      organisation: "Fonderie d'art Chapon à Bobigny",
      period: "Décembre 2020",
      details:
        "Stage dans une fonderie d'art de bronze. J'y ai appris des techniques comme le moulage ou la ciselure.",
    },
  ];

  readonly education: TimelineEntry[] = [
    {
      title: "BUT MMI (Métiers du Multimédia et de l'Internet)",
      organisation: "IUT de Meaux, Université Gustave Eiffel",
      period: "Septembre 2024 – Maintenant",
      details:
        "Spécialisation développement web et dispositifs interactifs. Formation qui lie audiovisuel et développement web.",
    },
    {
      title: "Baccalauréat technologique STI2D",
      organisation: "Lycée Pierre de Coubertin à Meaux",
      period: "Septembre 2022 – Juillet 2024",
      details:
        "Spécialité SIN (Systèmes d'Information et Numérique). Option audiovisuel. Bac avec mention Assez Bien.",
    },
  ];
}
<section id="parcours" class="container mx-auto px-6 py-20">
  <div class="max-w-7xl w-full mx-auto">
    <div class="mb-12">
      <h2 class="text-3xl mb-4">Parcours</h2>
      <p class="text-base max-w-3xl">
        Un aperçu de mon expérience professionnelle et de mon parcours scolaire,
        toujours guidé par la volonté de relier création visuelle et
        développement web.
      </p>
    </div>

    <div
      class="grid gap-10 md:grid-cols-[minmax(0,1fr)_auto_minmax(0,1fr)] items-stretch"
    >
      <div
        class="timeline-card p-8 bg-accent/5 rounded-2xl backdrop-blur-sm border border-white/10 h-full flex flex-col"
      >
        <h3 class="text-2xl mb-6">Expériences professionnelles</h3>
        <div
          *ngFor="let experience of experiences; let last = last"
          class="pb-6 mb-6 border-b border-white/10 last:mb-0 last:pb-0 last:border-none"
        >
          <h4 class="text-lg font-semibold mb-1">
            {{ experience.title }}
          </h4>
          <p class="italic mb-2">
            {{ experience.organisation }}
          </p>
          <p class="text-sm mb-3">
            {{ experience.period }}
          </p>
          <p class="leading-relaxed">
            {{ experience.details }}
          </p>
        </div>
      </div>

      <div class="hidden md:flex justify-center">
        <span class="w-px bg-white/10 rounded-full h-full"></span>
      </div>

      <div
        class="timeline-card p-8 bg-accent/5 rounded-2xl backdrop-blur-sm border border-white/10 h-full flex flex-col"
      >
        <h3 class="text-2xl mb-6">Formation &amp; Diplômes</h3>
        <div
          *ngFor="let diploma of education; let last = last"
          class="pb-6 mb-6 border-b border-white/10 last:mb-0 last:pb-0 last:border-none"
        >
          <h4 class="text-lg font-semibold mb-1">
            {{ diploma.title }}
          </h4>
          <p class="italic mb-2">
            {{ diploma.organisation }}
          </p>
          <p class="text-sm text-white/60 mb-3">
            {{ diploma.period }}
          </p>
          <p class="leading-relaxed">
            {{ diploma.details }}
          </p>
        </div>
      </div>
    </div>
  </div>
</section>
